package Codes;

public class SwapVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		        String x = "Cat";
				String y = "Dog";
				String swap;
				
				swap = x;
				x=y;
				y=swap;
				
				System.out.println("x:" +x);
			System.out.println("y:" +y);
						
	}

}
